document.addEventListener('DOMContentLoaded', function () {
    const btn = document.getElementById('download-btn');

    btn.addEventListener('click', function () {
        // Dummy data for the resume (replace with actual data)
        const resumeData = {
            name: 'Your Name',
            education: 'Candidate for Bachelor of Science in Computer Science and Behavioral Neuroscience',
            expertise: ['UX Design', 'Web Development'],
            // Add other resume data as needed
        };

        // Create a new PDF document
        const doc = new jsPDF();

        // Add content to the PDF
        doc.text(`Name: ${resumeData.name}`, 10, 10);
        doc.text(`Education: ${resumeData.education}`, 10, 20);
        doc.text('Expertise:', 10, 30);
        resumeData.expertise.forEach((item, index) => {
            doc.text(`- ${item}`, 20, 40 + index * 10);
        });

        // Save the PDF
        doc.save('resume.pdf');
    });
});
